import{gsap}from"gsap";import{ScrollTrigger}from"gsap/ScrollTrigger";gsap.registerPlugin(ScrollTrigger);const section2TL=gsap.timeline();section2Timeline.from(".container1 img",{duration:2,alpha:0,x:-50}).from(".container2 img",{duration:1,alpha:0,x:-50}).from(".container3 img",{duration:1,alpha:0,x:-50}).from(".container4 img",{duration:1,alpha:0,x:-50}).from(".container5 img",{duration:1,alpha:0,x:-50}).from(".container6 img",{duration:1,alpha:0,x:-50}).from(".container7 img",{duration:1,alpha:0,x:-50}).from(".container8 img",{duration:1,alpha:0,x:-50}).from(".container9 img",{duration:1,alpha:0,x:-50}).from(".container10 img",{duration:1,alpha:0,x:-50}).from(".container11 img",{duration:1,alpha:0,x:-50}).from(".container12 img",{duration:1,alpha:0,x:-50});export function section2Animation(){ScrollTrigger.create({markers:!0,animation:section2TL,toggleActions:"restart none reverse none",scrub:1,trigger:"#section-2",start:"top, 70%",end:"bottom, 70%"})}